import axios from 'axios'
import {useState, useEffect} from 'react'
const[data,setData] = useState([])


function useFetch(url){
    
    const fetchData = async () => {
        const res = await axios.get(url)
        setData(res.data)
    }

    useEffect(() => {
        fetchData()
    }, [url])

    return data
}

export default useFetch
