import axios from 'axios'
import {useState, useEffect} from 'react'
function useFetch(url){
    const[data,setData] = useState([])

    const fetchData = async () => {
        const res = await axios.get(url)
        setData(res.data)
    }

    useEffect(() => {
        fetchData()
    }, [url])

    return data
}

export default useFetch
